package jp.azuki.business.manager;import jp.azuki.core.lang.LoggingObject;/** * このクラスは、マネージャー機能の実装を行うための基底クラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/06/26 * @author Kawakicchi *  */public abstract class AbstractManager extends LoggingObject implements Manager {	/**	 * コンストラクタ	 */	public AbstractManager() {		super(Manager.class);	}	/**	 * コンストラクタ	 * 	 * @param name 名前	 */	public AbstractManager(final String name) {		super(name);	}	/**	 * コンストラクタ	 * 	 * @param clazz クラス	 */	public AbstractManager(final Class<?> clazz) {		super(clazz);	}}