package jp.azuki.core.log;import jp.azuki.persistence.context.Context;import org.apache.log4j.xml.DOMConfigurator;/** * このクラスは、Log4jロガーのインスタンス生成を行うファクトリクラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/02/09 * @author Kawakicchi */public final class Log4jLoggerFactory extends LoggerFactory {	/**	 * コンストラクタ	 */	public Log4jLoggerFactory() {	}	@Override	protected void doLoad(String aName, Context aContext) {		DOMConfigurator.configure(aContext.getAbstractPath(aName));	}	@Override	protected Logger doCreate() {		return new Log4jLogger(LoggerFactory.class);	}	@Override	protected Logger doCreate(final String aName) {		return new Log4jLogger(aName);	}	@Override	protected Logger doCreate(final Class<?> aClass) {		return new Log4jLogger(aClass);	}}