package jp.azuki.persistence;/** * このクラスは、設定フォーマットの異常を表現する例外クラスです。 *  * @since 1.0.0 * @version 1.0.0 12/07/02 * @author Kawakicchi */public class ConfigurationFormatException extends PersistenceServiceException {	/**	 * serialVersionUID	 */	private static final long serialVersionUID = -5944051273106639399L;	/**	 * コンストラクタ	 * 	 * @param message Message	 */	public ConfigurationFormatException(final String message) {		super(message);	}	/**	 * コンストラクタ	 * 	 * @param throwable Throwable	 */	public ConfigurationFormatException(final Throwable throwable) {		super(throwable);	}	/**	 * コンストラクタ	 * 	 * @param message Message	 * @param throwable Throwable	 */	public ConfigurationFormatException(final String message, final Throwable throwable) {		super(message, throwable);	}}