package jp.azuki.persistence.configuration;import java.io.InputStream;/** * このクラスは、コンフィグレーション機能の実装クラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/19 * @author Kawakicchi */public class InputStreamConfiguration extends AbstractConfiguration {	/**	 * Stream	 */	private InputStream stream;	/**	 * コンストラクタ	 * 	 * @param aStream	 */	public InputStreamConfiguration(final InputStream aStream) {		stream = aStream;	}	@Override	public InputStream getResourceAsStream() {		return stream;	}}