package jp.azuki.mikeneko.account;import jp.azuki.persistence.proterty.Property;/** * このクラスは、アカウント情報を保持するクラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/05 * @author Kawakicchi */public class Account {	/**	 * アカウントID	 */	private String id;	/**	 * アカウント名	 */	private String name;	/**	 * プロパティ情報	 */	private Property property;	/**	 * コンストラクタ	 */	public Account() {	}	/**	 * アカウントIDを取得する。	 * 	 * @return アカウントID	 */	public String getId() {		return id;	}	/**	 * アカウント名を取得する。	 * 	 * @return アカウント名	 */	public String getName() {		return name;	}	/**	 * プロパティ情報を取得する。	 * 	 * @return プロパティ情報	 */	public Property getProperty() {		return property;	}}