package jp.azuki.mikeneko.authentication;/** * このクラスは、認証機能の実装を行うための基底クラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/01 * @author Kawakicchi *  */public abstract class AbstractAuthentication implements Authentication {	/**	 * コンストラクタ	 */	public AbstractAuthentication() {	}	/**	 * コンストラクタ	 * 	 * @param aName 名前	 */	public AbstractAuthentication(final String aName) {	}	/**	 * コンストラクタ	 * 	 * @param aClass クラス	 */	public AbstractAuthentication(final Class<?> aClass) {	}	@Override	public void authenticate() {		doAuthenticate();	}	protected abstract void doAuthenticate();}