package jp.azuki.mikeneko.authentication;/** * このクラスは、認証機能の実装を行うための基底クラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/01 * @author Kawakicchi *  */public abstract class AbstractAuthentication implements Authentication {	/**	 * コンストラクタ	 */	public AbstractAuthentication() {	}	/**	 * コンストラクタ	 * 	 * @param aName 名前	 */	public AbstractAuthentication(final String aName) {	}	/**	 * コンストラクタ	 * 	 * @param aClass クラス	 */	public AbstractAuthentication(final Class<?> aClass) {	}	@Override	public boolean signin() {		return doSignin();	}	@Override	public boolean check() {		return doCheck();	}	@Override	public boolean signout() {		return doSignout();	}	/**	 * 認証を開始する。	 * 	 * @return 認証結果	 */	protected abstract boolean doSignin();	/**	 * 認証を開始しているか判断する。	 * 	 * @return 認証判断結果	 */	protected abstract boolean doCheck();	/**	 * 認証を終了する。	 * 	 * @return 認証結果	 */	protected abstract boolean doSignout();}