package jp.azuki.mikeneko.authentication;/** * このインターフェースは、認証機能を表現するインターフェースです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/01 * @author Kawakicchi *  */public interface Authentication {	/**	 * 認証を開始する。	 * 	 * @return 認証結果	 */	public boolean signin();	/**	 * 認証を開始しているか判断する。	 * 	 * @return 認証判断結果	 */	public boolean check();	/**	 * 認証を終了する。	 * 	 * @return 認証結果	 */	public boolean signout();}