package jp.azuki.mikeneko.field;public class AbstractField {	public enum Condition {		Normal(0, ""), Success(1, "success"), Error(2, "error"), Warning(3, "warning");		private int state;		private String name;		private Condition(final int aState, final String aName) {			state = aState;			name = aName;		}		public int getState() {			return state;		}		public String getName() {			return name;		}	}	private Condition condition;	private String help;	public AbstractField() {	}	public final void setCondition(final Condition aCondition) {		condition = aCondition;	}	public final Condition getCondition() {		return condition;	}	public final void setHelp(final String aHelp) {		help = aHelp;	}	public final String getHelp() {		return help;	}}