package jp.azuki.mikeneko.logic;import jp.azuki.business.logic.AbstractLogic;import jp.azuki.mikeneko.account.Account;import jp.azuki.mikeneko.menu.Menu;import jp.azuki.mikeneko.menu.MenuBar;import jp.azuki.mikeneko.menu.MenuItem;public class MenuManagerImpl extends AbstractLogic implements MenuManager {	public MenuManagerImpl() {		super(MenuManagerImpl.class);	}	@Override	protected void doInitialize() {	}	@Override	protected void doDestroy() {	}	@Override	public MenuBar generate(final Account aAccount) {		MenuBar bar = new MenuBar("NCO Project");		Menu menu = null;		MenuItem item = null;		menu = new Menu("ホーム", "/home.do");		bar.add(menu);		menu = new Menu("シミュレーション", "/simulation.do");		bar.add(menu);		menu = new Menu("検索", "/simulation/search.do");		bar.add(menu);		menu = new Menu("ファイル");		item = new MenuItem("検索", "/file/search.do");		menu.add(item);		item = new MenuItem("登録", "/file/regist.do");		menu.add(item);		bar.add(menu);				menu = new Menu("管理");		item = new MenuItem("アカウント", "/manager/account.do");		menu.add(item);		bar.add(menu);		return bar;	}}