package jp.azuki.mikeneko.menu;import java.util.ArrayList;import java.util.List;import jp.azuki.core.util.StringUtility;/** * このクラスは、メニュー情報を保持するクラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/05 * @author Kawakicchi */public class Menu extends MenuItem {	private List<MenuItem> items;	public Menu(final String aLabel) {		super(aLabel, StringUtility.EMPTY);		items = new ArrayList<>();	}	public Menu(final String aLabel, final String aUrl) {		super(aLabel, aUrl);		items = new ArrayList<>();	}	public MenuItem add(final MenuItem aItem) {		items.add(aItem);		return aItem;	}	public boolean hasItem() {		return (0 != items.size());	}	public List<MenuItem> getMenuItemList() {		return items;	}}