package jp.azuki.mikeneko.menu;import java.util.ArrayList;import java.util.List;/** * このクラスは、メニューバー情報を保持するクラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/05 * @author Kawakicchi */public class MenuBar {	private String title;	private List<Menu> menus;	/**	 * コンストラクタ	 */	public MenuBar() {		menus = new ArrayList<>();	}	/**	 * コンストラクタ	 * 	 * @param aTitle タイトル	 */	public MenuBar(final String aTitle) {		title = aTitle;		menus = new ArrayList<>();	}	/**	 * メニューを追加する。	 * 	 * @param aMenu メニュー	 * @return メニュー	 */	public Menu add(final Menu aMenu) {		menus.add(aMenu);		return aMenu;	}	public String getTitle() {		return title;	}	public List<Menu> getMenuList() {		return menus;	}}