package jp.azuki.mikeneko.menu;/** * このクラスは、メニューアイテム情報を保持するクラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/05 * @author Kawakicchi */public class MenuItem {	private String label;	private String url;	public MenuItem(final String aLabel, final String aUrl) {		label = aLabel;		url = aUrl;	}	public String getLabel() {		return label;	}	public String getUrl() {		return url;	}}