package jp.azuki.mikeneko.message;/** * このクラスは、アラートメッセージ情報を保持するクラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/09 * @author Kawakicchi */public final class AlertMessage {	public static enum Type {		/**		 * 情報		 */		information("情報", 1),		/**		 * 警告		 */		warning("警告", 2),		/**		 * 成功		 */		success("成功", 3),		/**		 * エラー		 */		error("エラー", 4);		private String name;		private int value;		private Type(final String aName, final int aValue) {			name = aName;			value = aValue;		}		public String getName() {			return name;		}		public int getValue() {			return value;		}	}	/**	 * メッセージタイプ	 */	private Type type;	/**	 * メッセージ	 */	private String message;	/**	 * コンストラクタ	 * 	 * @param aType メッセージタイプ	 * @param aMessage メッセージ	 */	public AlertMessage(final Type aType, final String aMessage) {		type = aType;		message = aMessage;	}	/**	 * メッセージタイプを取得する。	 * 	 * @return メッセージタイプ	 */	public Type getType() {		return type;	}	/**	 * メッセージを取得する。	 * 	 * @return メッセージ	 */	public String getMessage() {		return message;	}}