package jp.azuki.mikeneko.web.display;import jp.azuki.mikeneko.message.AlertMessage;public class Display {	private String token;	private AlertMessage alertMessage;	public Display(final String aToken) {		token = aToken;	}	public String getToken() {		return token;	}	public void setAlertMessage(final AlertMessage aAlert) {		alertMessage = aAlert;	}	public AlertMessage getAlertMessage() {		return alertMessage;	}}