package jp.azuki.mikeneko.web.display;import java.util.ArrayList;import java.util.List;public class DisplayStack {	private List<Display> displays;	public DisplayStack() {		displays = new ArrayList<>();	}	public Display pop() {		Display display = null;		if (0 < displays.size()) {			display = displays.get(displays.size() - 1);			displays.remove(displays.size() - 1);		}		return display;	}	public void push(final Display aDiaplay) {		displays.add(aDiaplay);	}	public List<Display> getDisplayList() {		return new ArrayList<>(displays);	}}