package jp.azuki.mikeneko.web.model;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import jp.azuki.web.view.View;/** * このインターフェースは、ビジネスモデルを表現するインターフェースです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/05 * @author Kawakicchi */public interface BusinessModel {	/**	 * モデルを実行する。	 * 	 * @param aReq リクエスト情報	 * @param aRes レスポンス情報	 * @return ビュー情報	 */	public View execute(final HttpServletRequest aReq, final HttpServletResponse aRes);}