package jp.azuki.mikeneko.web.model;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import jp.azuki.mikeneko.web.model.data.DisplayModelData;import jp.azuki.web.view.JSPView;import jp.azuki.web.view.View;/** * このクラスは、表示ビジネスモデルの実装を行うための基底クラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/05 * @author Kawakicchi */public abstract class DisplayBusinessModel<DISPLAY_DATA extends DisplayModelData> extends AbstractBusinessModel {	/**	 * コンストラクタ	 */	public DisplayBusinessModel() {		super(DisplayBusinessModel.class);	}	/**	 * コンストラクタ	 * 	 * @param aName 名前	 */	public DisplayBusinessModel(final String aName) {		super(aName);	}	/**	 * コンストラクタ	 * 	 * @param aClass クラス	 */	public DisplayBusinessModel(final Class<?> aClass) {		super(aClass);	}	@Override	protected final View doExecute(final HttpServletRequest aReq, final HttpServletResponse aRes) throws IllegalAccessException, InstantiationException {		View view = null;				Class<DISPLAY_DATA> clazz = getDisplayModelData();		DISPLAY_DATA data = clazz.newInstance();		doInit(data);				view = new JSPView("home/home.jsp");				return view;	}	protected abstract Class<DISPLAY_DATA> getDisplayModelData();	/**	 * 初期化処理を行う。	 */	protected abstract void doInit(final DISPLAY_DATA aDisplay);}