package jp.azuki.mikeneko.web.model;import jp.azuki.mikeneko.web.model.data.DisplayModelData;/** * このクラスは、表示ビジネスモデルの実装を行うための基底クラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/05 * @author Kawakicchi */public abstract class DisplayBusinessModel extends AbstractBusinessModel {	/**	 * コンストラクタ	 */	public DisplayBusinessModel() {		super(DisplayBusinessModel.class);	}	/**	 * コンストラクタ	 * 	 * @param aName 名前	 */	public DisplayBusinessModel(final String aName) {		super(aName);	}	/**	 * コンストラクタ	 * 	 * @param aClass クラス	 */	public DisplayBusinessModel(final Class<?> aClass) {		super(aClass);	}	@Override	protected final void doExecute() throws IllegalAccessException, InstantiationException {		Class<? extends DisplayModelData> clazz = getDisplayModelData();		DisplayModelData data = clazz.newInstance();		doInit(data);	}	protected abstract Class<? extends DisplayModelData> getDisplayModelData();	/**	 * 初期化処理を行う。	 */	protected void doInit(final DisplayModelData data) {	}}