package jp.azuki.mikeneko.web.model;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import jp.azuki.mikeneko.web.model.data.SignoutModelData;import jp.azuki.web.view.JSPView;import jp.azuki.web.view.View;/** * このクラスは、サインアウトビジネスモデルの実装を行うための基底クラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/09 * @author Kawakicchi */public abstract class SignoutBusinessModel<SIGNOUT_DATA extends SignoutModelData> extends AbstractBusinessModel {	/**	 * コンストラクタ	 */	public SignoutBusinessModel() {		super(SignoutBusinessModel.class);	}	/**	 * コンストラクタ	 * 	 * @param aName 名前	 */	public SignoutBusinessModel(final String aName) {		super(aName);	}	/**	 * コンストラクタ	 * 	 * @param aClass クラス	 */	public SignoutBusinessModel(final Class<?> aClass) {		super(aClass);	}	@Override	protected final View doExecute(final HttpServletRequest aReq, final HttpServletResponse aRes) throws IllegalAccessException,			InstantiationException {		View view = null;		Class<SIGNOUT_DATA> clazz = getSignoutModelData();		SIGNOUT_DATA data = clazz.newInstance();		doSignout(data);		view = new JSPView("signin.jsp");		return view;	}	protected abstract Class<SIGNOUT_DATA> getSignoutModelData();	/**	 * サインアウト処理を行う。	 * 	 * @param aSignout サインアウト情報	 */	protected abstract void doSignout(final SIGNOUT_DATA aSignout);}