package jp.azuki.mikeneko.web.session;import jp.azuki.mikeneko.account.Account;import jp.azuki.mikeneko.web.display.DisplayStack;/** * このクラスは、ユーザセッション情報を保持するクラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/09 * @author Kawakicchi */public class MikeNekoUserSession {	/**	 * アカウント情報	 */	private Account account;	/**	 * 画面スタック情報	 */	private DisplayStack displayStack;	/**	 * コンストラクタ	 * 	 * @param aAccount アカウント情報	 */	public MikeNekoUserSession(final Account aAccount) {		account = aAccount;		displayStack = new DisplayStack();	}	/**	 * アカウント情報を取得する。	 * 	 * @return アカウント情報	 */	public Account getAccount() {		return account;	}	/**	 * 画面スタック情報を取得する。	 * 	 * @return 画面スタック情報	 */	public DisplayStack getDisplayStack() {		return displayStack;	}}