package jp.azuki.mikeneko.web.tags;import javax.servlet.jsp.JspException;import jp.azuki.web.constant.WebConstant;import jp.azuki.web.tags.AbstractRenderingTag;/** * このクラスは、バックボタンを表示するタグです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/11 * @author Kawakicchi */public class BackButtonTag extends AbstractRenderingTag {	@Override	protected void doRendering(final StringBuffer aRender) throws JspException {		aRender.append("<a class=\"btn\" href=\"").append(WebConstant.getUrl("/back.do")).append("\">Back</a>");	}}