package jp.azuki.web.pattern;/** * このクラスは、Webビジネス変更パターン機能の実装を行うための基底クラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/01 * @author Kawakicchi */public abstract class AbstractModifyPattern extends AbstractBusinessPattern {	private int status;	private static final int STATUS_INIT = 0;	private static final int STATUS_CHECK = 1;	private static final int STATUS_MODIFY = 2;	private static final int STATUS_COMPLETE = 3;	@Override	protected final void doPattern() {		switch (status) {		case STATUS_INIT:			doInit();			break;		case STATUS_CHECK:			doCheck();			break;		case STATUS_MODIFY:			doModify();			break;		default:			doInit();			break;		}	}	/**	 * 画面初期表示前に呼び出される。	 * (GET)	 */	protected void doInit() {	}	/**	 * 更新事前確認	 */	protected void doCheck() {	}	/**	 * 更新	 */	protected void doModify() {	}}