package jp.azuki.web.pattern;import jp.azuki.core.lang.LoggingObject;/** * このクラスは、Webビジネスパターン機能の実装を行うための基底クラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/01 * @author Kawakicchi */public abstract class AbstractPattern extends LoggingObject implements Pattern {	/**	 * コンストラクタ	 */	public AbstractPattern() {		super(Pattern.class);	}	/**	 * コンストラクタ	 * 	 * @param aName 名前	 */	public AbstractPattern(final String aName) {		super(aName);	}	/**	 * コンストラクタ	 * 	 * @param aClass クラス	 */	public AbstractPattern(final Class<?> aClass) {		super(aClass);	}	@Override	public void pattern() {		doPattern();	}	/**	 * パターン処理を実行する。	 */	protected abstract void doPattern();}