package jp.azuki.web.tags.html;import jp.azuki.core.util.StringUtility;public abstract class AbstractInputTag extends AbstractHtmlTag {	/**	 * name	 */	private String name = null;	/**	 * コンストラクタ	 * 	 */	public AbstractInputTag() {		super(AbstractHtmlTag.class);	}	/**	 * コンストラクタ	 * 	 * @param aName 名前	 */	public AbstractInputTag(final String aName) {		super(aName);	}	/**	 * コンストラクタ	 * 	 * @param aClass クラス	 */	public AbstractInputTag(final Class<?> aClass) {		super(aClass);	}	public final void setName(final String aName) {		name = aName;	}	protected final String getName() {		return name;	}	@Override	protected final String getTagName() {		return "input";	}	protected abstract String getType();	@Override	protected void doAppendAttributes() {		super.doAppendAttributes();		Object value = null;		if (StringUtility.isNotEmpty(getName())) {			value = getRequestAttribute(getName());		}		addAttribute("name", getName());		addAttribute("type", getType());		addAttribute("value", StringUtility.toStringEmpty(value));	}}