package jp.azuki.web.tags.logic;import jp.azuki.web.tags.AbstractTag;/** * このクラスは、ロジックタグクラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/07/12 * @author Kawakicchi */public abstract class AbstractLogicTag extends AbstractTag {	/**	 * コンストラクタ	 */	public AbstractLogicTag() {		super(AbstractLogicTag.class);	}	/**	 * コンストラクタ	 * 	 * @param aName 名前	 */	public AbstractLogicTag(final String aName) {		super(aName);	}	/**	 * コンストラクタ	 * 	 * @param aClass クラス	 */	public AbstractLogicTag(final Class<?> aClass) {		super(aClass);	}}